CREATE TABLE IF NOT EXISTS `uta_glarticle` (
  `id` mediumint(9) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `title` varchar(500) NOT NULL COMMENT '攻略文章标题',
  `destination` varchar(300) NOT NULL COMMENT '攻略介绍的地点',
  `content` text NOT NULL COMMENT '文章内容',
  `summary` mediumtext NOT NULL COMMENT '文章简介',
