--
-- 表的结构 `uta_user`
--

CREATE TABLE IF NOT EXISTS `uta_user` (
  `id` mediumint(9) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `email` varchar(60) NOT NULL COMMENT '用户邮箱,登录账号',
  `password` varchar(255) NOT NULL COMMENT '密码',
  `portrait` varchar(255) NOT NULL DEFAULT 'moren\\moren.jpg' COMMENT '用户头像路径',
  `address` varchar(255) NOT NULL COMMENT '用户城市',
  `sign` text NOT NULL COMMENT '用户个性签名',
  `sex` tinyint(4) NOT NULL DEFAULT '1' COMMENT '用户性别:1,男;2,女;',
  `tel` varchar(30) NOT NULL COMMENT '电话号码',
  `qq` varchar(30) NOT NULL COMMENT 'qq',
  `wechat` varchar(60) NOT NULL COMMENT '微信号',
  `tname` varchar(150) NOT NULL COMMENT '真实姓名',
  `university` varchar(255) NOT NULL COMMENT '所在大学',
  `jianjie` text NOT NULL COMMENT '个人简介',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '用户状态:1,普通用户;2,导游;3,待审核;',
  `uname` varchar(50) NOT NULL COMMENT '用户昵称',
  `sqtime` varchar(50) NOT NULL,
  `regtime` varchar(50) NOT NULL COMMENT '注册时间',
  `click` mediumint(9) NOT NULL DEFAULT '0' COMMENT '点击量',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='用户信息表' AUTO_INCREMENT=28 ;

